(15246, 2527, '_ig_page_builder_content', '[ig_row width="boxed" r_class="none" t_class="none" background="none" solid_color_value="#FFFFFF" solid_color_color="#ffffff" gradient_color="0% #FFFFFF,100% #000000"  gradient_direction="vertical"  repeat="full"  stretch="none" position="center center" paralax="no" relative="no" video_b="no"    v_mp4 border_width_value_="0" border_style="solid" border_color="#000"  div_padding_top="30" div_padding_bottom="30" div_padding_right="30" div_padding_left="30"   ][ig_column span="span12"][ig_heading el_title="New Features in ver. 2.0" tag="h5" text_align="inherit" font="inherit" border_bottom_width_value_="1" border_bottom_style="solid" border_bottom_color="#e1e1e1" padding_bottom_value_="10" disabled_el="no" ]New Features in ver. 2.0[/ig_heading][/ig_column][/ig_row][ig_row width="boxed" r_class="none" t_class="none" background="none" solid_color_value="#FFFFFF" solid_color_color="#ffffff" gradient_color="0% #FFFFFF,100% #000000"  gradient_direction="vertical"  repeat="full"  stretch="none" position="center center" paralax="no" relative="no" video_b="no"    v_mp4 border_width_value_="0" border_style="solid" border_color="#000"  div_padding_top="30" div_padding_bottom="30" div_padding_right="30" div_padding_left="30"   ][ig_column span="span4"][ig_text enable_dropcap="no" disabled_el="no" ]<ul class="ig-list-icons  ig-position-left ig-shape-circle ig-icon-24">\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-check-square-o"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">SEO Friendly</h4>\r\n<div class="ig-list-content">\r\nShema.org microdata on each page\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-link"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">New Breadcrumbs</h4>\r\n<div class="ig-list-content">\r\nShema.org added to breadcrumbs\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-map-marker"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">Updated PT Contacts widget</h4>\r\n<div class="ig-list-content">\r\nNow with microdata & logo upload option\r\n</div>\r\n</div>\r\n</li>\r\n</ul>[/ig_text][/ig_column][ig_column span="span4"][ig_text enable_dropcap="no" disabled_el="no" ]<ul class="ig-list-icons  ig-position-left ig-shape-circle ig-icon-24">\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-sign-in"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">New Login/Register widget</h4>\r\n<div class="ig-list-content">\r\nStylish & lightweight widget\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-search"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">Updated PT Search widget</h4>\r\n<div class="ig-list-content">\r\nYou can now search in specified categories\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-filter"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">Horizontal Shop Filters</h4>\r\n<div class="ig-list-content">\r\nAdded new sidebar for shop filters\r\n</div>\r\n</div>\r\n</li>\r\n</ul>[/ig_text][/ig_column][ig_column span="span4"][ig_text enable_dropcap="no" disabled_el="no" ]<ul class="ig-list-icons  ig-position-left ig-shape-circle ig-icon-24">\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-puzzle-piece"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">New Pagebuilder Elements</h4>\r\n<div class="ig-list-content">\r\nCircle Bars, Pricing Tables, Vendor lists\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-users"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">WC Vendors update</h4>\r\n<div class="ig-list-content">\r\nMore control options for vendors\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-lock"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">WC Vendors widget</h4>\r\n<div class="ig-list-content">\r\nAdd dynamic widget for Vendors Page\r\n</div>\r\n</div>\r\n</li>\r\n</ul>[/ig_text][/ig_column][/ig_row][ig_row width="boxed" r_class="none" t_class="none" background="none" solid_color_value="#FFFFFF" solid_color_color="#ffffff" gradient_color="0% #FFFFFF,100% #000000"  gradient_direction="vertical"  repeat="full"  stretch="none" position="center center" paralax="no" relative="no" video_b="no"    v_mp4 border_width_value_="0" border_style="solid" border_color="#000"  div_padding_top="30" div_padding_bottom="30" div_padding_right="30" div_padding_left="30"   ][ig_column span="span12"][ig_heading el_title="Basic Features" tag="h5" text_align="inherit" font="inherit" border_bottom_width_value_="1" border_bottom_style="solid" border_bottom_color="#e1e1e1" padding_bottom_value_="10" disabled_el="no" ]Basic Features[/ig_heading][/ig_column][/ig_row][ig_row width="boxed" r_class="none" t_class="none" background="none" solid_color_value="#FFFFFF" solid_color_color="#ffffff" gradient_color="0% #FFFFFF,100% #000000" gradient_direction="vertical" repeat="full" stretch="none" position="center center" paralax="no" relative="no" video_b="no" v_mp4 border_width_value_="0" border_style="solid" border_color="#000" div_padding_top="30" div_padding_bottom="30" div_padding_right="30" div_padding_left="30" ][ig_column span="span3"][ig_list el_title="Customizable Header" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Customizable Header" icon="icon-home" disabled_el="no" ]From theme settings you can completely change the way your header looks, or even remove elements you don''t need. You can replace the logo and change it''s position, add new widgets, change the background etc.<br>[/ig_item_list][/ig_list][ig_list el_title="Bootstrap 3" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Bootstrap 3" icon="icon-screen" disabled_el="no" ]The whole theme is built on Bootstrap 3.<br>Font Awesome and DashIcons are used in the theme.<br>Handy is a 4-column theme, adopted for mobile devices<br>[/ig_item_list][/ig_list][ig_list el_title="Woocommerce" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Woocommerce" icon="icon-cart" disabled_el="no" ]Hadny theme was created as a WooCommerce store. In this theme be broadened WooCoommerce functionality and allowed basic store parameters contol from the theme settings menu.<br>[/ig_item_list][/ig_list][ig_list el_title="PageBuilder" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="PageBuilder" icon="icon-cube" disabled_el="no" ]Unique elements which are available in the PageBuilder plugin which comes with the theme. All static pages of our live demo were built in PageBuilder.[/ig_item_list][/ig_list][ig_list el_title="Product image slider" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Product image slider" icon="icon-eye" disabled_el="no" ]Product image slider has 3 variants of display: single item slider, single item slider with pop-up gallery, gallery with thumbs.<br>[/ig_item_list][/ig_list][ig_list el_title="Gallery & Portfolio" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Gallery & Portfolio" icon="icon-camera" disabled_el="no" ]Separate templates with different designs for galleries and portfolio are included in the theme.[/ig_item_list][/ig_list][ig_list el_title="WooCommerce shortcodes" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="WooCommerce shortcodes" icon="icon-cogs" disabled_el="no" ]We integrated WooCommerce shortcodes with PageBuilder so they became even easier to use.<br>[/ig_item_list][/ig_list][ig_list el_title="Breadcrumbs & Navigation" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" disabled_el="no" ][ig_item_list heading="Breadcrumbs & Navigation" icon="icon-out" disabled_el="no" ]You can always turn on and off these elements in theme settings.<br>[/ig_item_list][/ig_list][/ig_column][ig_column span="span3"][ig_list el_title="Post Like System" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Post Like System" icon="icon-thumbs-up" disabled_el="no" ]We created an easy mechanism of likes for posts and products. The whole system inclued 2 new widgets (product/post output based on like quantity, and a list of user''s likes, which can be seen by logged in users only)<br>[/ig_item_list][/ig_list][ig_list el_title="Layouts for pages" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Layouts for pages" icon="icon-copy" disabled_el="no" ]Choosing individual page layout is an easy thing now. <br>Available options: <br>1. one-column layout <br>2. 2 columns + left sidebar <br>3. 2 columns + right sidebar<br>[/ig_item_list][/ig_list][ig_list el_title="Custom badges for products" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Custom badges for products" icon="icon-bookmark" disabled_el="no" ]Add whatever badges right from the product editing page. You can add any text for products (on sale, top product, we recommend) and add a css class for that block[/ig_item_list][/ig_list][ig_list el_title="Share Buttons" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Share Buttons" icon="icon-out-2" disabled_el="no" ]You can add share buttons without additional plugins. Twitter, Facebook, Pinterest and Google+ with share counters are available.[/ig_item_list][/ig_list][ig_list el_title="Widgets" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Widgets" icon="icon-puzzle" disabled_el="no" ]Handy included a whole set (11pcs) of new widgets which make it easier to fill your site with content.[/ig_item_list][/ig_list][ig_list el_title="Suppamenu" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Suppamenu" icon="icon-menu" disabled_el="no" ]It''s a great plugin for creating various mega menus. You won''t have to pay for it - it comes with the theme.[/ig_item_list][/ig_list][ig_list el_title="Carousel" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Carousel" icon="icon-loop" disabled_el="no" ]Add a carousel block to any page using PageBuilder. Manage each carousel content separately.[/ig_item_list][/ig_list][ig_list el_title="Theme settings" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Theme Settings" icon="icon-dashboard" disabled_el="no" ]A settings control system for maximal user comfort is included<br>[/ig_item_list][/ig_list][/ig_column][ig_column span="span3"][ig_list el_title="Custom Colors & Fonts" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Custom Colors & Fonts" icon="icon-color-palette" disabled_el="no" ]It''s easy to change the color scheme of your store now. There''s a section of theme settings where you can find 14 options to set colors and fonts for headings, main content, links and buttons<br>[/ig_item_list][/ig_list][ig_list el_title="Prestyled post formats" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Prestyled post formats" icon="icon-warning" disabled_el="no" ]Unique layouts have been created for post types gallery and audio of handy theme. All theme is built according to the latest trends of design and technology to assure great user experience[/ig_item_list][/ig_list][ig_list el_title="Shop filters" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Shop filters" icon="icon-filter" disabled_el="no" ]With Handy theme you get an opportunity to create any product filters with the help of attributes. And filtration itself is awesome ''cause it''s built on Isotope.[/ig_item_list][/ig_list][ig_list el_title="Advanced product" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Advanced product" icon="icon-wand" disabled_el="no" ]Theme features 2 animated product hover effects - fading and sliding. There''s also a possibility to use an extra image gallery for product listing.[/ig_item_list][/ig_list][ig_list el_title="Testimonials" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Testimonials" icon="icon-users" disabled_el="no" ]Add a testimonial block to any page using PageBuilder. It''s a comfortable and easy-to use widget.<br>[/ig_item_list][/ig_list][ig_list el_title="Isotope" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Isotope" icon="icon-checkmark" disabled_el="no" ]themes.zone developers love this incredible jQuery plugin. all filters of this theme are built on Isotope[/ig_item_list][/ig_list][ig_list el_title="List/grid for products" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="List/grid for products" icon="icon-list-view" disabled_el="no" ]There''s a possibility of switching the product view on the store page.<br>[/ig_item_list][/ig_list][/ig_column][ig_column span="span3"][ig_list el_title="Handy Special Elements" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Handy Special Elements" icon="icon-lightning" disabled_el="no" ]A set of unique elements has been created for Handy theme. <br>7 different shortcodes are going to make your store unique. <br>You can see all available features in action at Handy Store Elements page.[/ig_item_list][/ig_list][ig_list el_title="Maintenance mode" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Maintenance mode" icon="icon-wrench" disabled_el="no" ]You can turn this mode on without additional plugins. You can also set the estimated launch time so that a countdown timer will appear on your under construction page.<br>[/ig_item_list][/ig_list][ig_list el_title="PageBuilder Bannder" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="PageBuilder Banner" icon="icon-picture" disabled_el="no" ]This element is highly effective for creating ad banners for your store. All banners on our Live Demo are based on this element. You can find more info in the theme docs.[/ig_item_list][/ig_list][ig_list el_title="Sales carousel" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Sales carousel" icon="icon-basket" disabled_el="no" ]Create your own product carousel with special prices. Set sale duration time and a countdown timer will appear near the product.[/ig_item_list][/ig_list][ig_list el_title="Slider Revolution" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Slider Revolution" icon="icon-pictures" disabled_el="no" ]Slider Revolution is a great plugin for creating sliders. You won''t have to pay for it - it comes with the theme.[/ig_item_list][/ig_list][ig_list el_title="Blog Layouts" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Blog Layouts" icon="icon-grid-view" disabled_el="no" ]You can choose between one column and multi column layouts. You can also add a post filtration system - by tags or by categories<br>[/ig_item_list][/ig_list][ig_list el_title="Google fonts" show_icon="yes" icon_position="left" icon_size_value="24" icon_background_type="circle" icon_background_color="#ffffff" icon_c_value="#c2d44e" icon_c_color="#c2d44e" show_heading="yes" font="custom" font_face_type="google fonts" font_face_value="Roboto" font_size_value="15" font_style="bold" color="#000000" ][ig_item_list heading="Google fonts" icon="icon-pencil-2" disabled_el="no" ]You can now choose any font from over 600 Google Fonts library[/ig_item_list][/ig_list][/ig_column][/ig_row]'),
(15247, 2527, '_ig_html_content', '<div class="container  "><div  class=''row '' ><div class="col-md-12 col-sm-18 col-xs-12" ><h5 style=''border-bottom-width:1px;border-bottom-style:solid;border-bottom-color:#e1e1e1;padding-bottom:10px;''>New Features in ver. 2.0</h5></div></div></div><div class="container  "><div  class=''row '' ><div class="col-md-4 col-sm-6 col-xs-12" ><div class="ig_text "><ul class="ig-list-icons  ig-position-left ig-shape-circle ig-icon-24">\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-check-square-o"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">SEO Friendly</h4>\r\n<div class="ig-list-content">\r\nShema.org microdata on each page\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-link"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">New Breadcrumbs</h4>\r\n<div class="ig-list-content">\r\nShema.org added to breadcrumbs\r\n</div>\r\n</div>\r\n</li>\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-map-marker"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Verdana;font-size: 15px;color: #000000;font-weight: 700 !important">Updated PT Contacts widget</h4>\r\n<div class="ig-list-content">\r\nNow with microdata & logo upload option\r\n</div>\r\n</div>\r\n</li>\r\n</ul></div></div><div class="col-md-4 col-sm-6 col-xs-12" ><div class="ig_text "><ul class="ig-list-icons  ig-position-left ig-shape-circle ig-icon-24">\r\n<li>\r\n<div class="ig-icon-base" style="background-color: #c1d449;color: #ffffff">\r\n		<i class="fa fa-sign-in"></i>\r\n	</div>\r\n<div class="ig-list-content-wrap">\r\n<h4 style="font-family: Ve